// controllers/FilesController.js
import { promises as fs } from 'fs';
import path from 'path';
import mime from 'mime-types';
import { ObjectId } from 'mongodb';
import { v4 as uuidv4 } from 'uuid';
import redisClient from '../utils/redis';
import dbClient from '../utils/db';

/* constants */
const TMP_FOLDER = process.env.FOLDER_PATH || '/tmp/files_manager';
const PAGE_SIZE   = 20;
const VALID_TYPES = ['folder', 'file', 'image'];

export default class FilesController {
/* -------------------------------------------------------------------------- */
/* Helper utilities                                                           */
/* -------------------------------------------------------------------------- */
  static async userFromToken(req) {
    const token = req.header('X-Token');
    if (!token) return null;
    const userId = await redisClient.get(`auth_${token}`);
    if (!userId) return null;
    return dbClient.usersCollection().findOne({ _id: ObjectId(userId) });
  }

  static sanitize(doc) {
    return {
      id:        doc._id.toString(),
      userId:    doc.userId.toString(),
      name:      doc.name,
      type:      doc.type,
      isPublic:  doc.isPublic,
      parentId:  doc.parentId,
    };
  }

/* -------------------------------------------------------------------------- */
/* 5 – POST /files  (already implemented elsewhere)                           */
/* -------------------------------------------------------------------------- */
/* -------------------------------------------------------------------------- */
/* 6 – GET /files/:id                                                         */
/* -------------------------------------------------------------------------- */
  static async getShow(req, res) {
    const user = await FilesController.userFromToken(req);
    if (!user) return res.status(401).json({ error: 'Unauthorized' });

    let file;
    try {
      file = await dbClient.filesCollection().findOne({
        _id: ObjectId(req.params.id),
        userId: user._id,
      });
    } catch (e) {
      return res.status(404).json({ error: 'Not found' });
    }
    if (!file) return res.status(404).json({ error: 'Not found' });

    return res.json(FilesController.sanitize(file));
  }

/* -------------------------------------------------------------------------- */
/* 6 – GET /files                                                             */
/* -------------------------------------------------------------------------- */
  static async getIndex(req, res) {
    const user = await FilesController.userFromToken(req);
    if (!user) return res.status(401).json({ error: 'Unauthorized' });

    const parentId = req.query.parentId || 0;
    const page     = Number.parseInt(req.query.page || '0', 10);

    const match = { userId: user._id, parentId };
    if (parentId === 0) delete match.parentId;   // root: no filter

    const pipeline = [
      { $match: match },
      { $skip: page * PAGE_SIZE },
      { $limit: PAGE_SIZE },
    ];

    const docs = await dbClient.filesCollection().aggregate(pipeline).toArray();
    return res.json(docs.map((d) => FilesController.sanitize(d)));
  }

/* -------------------------------------------------------------------------- */
/* 7 – PUT /files/:id/publish & /unpublish                                    */
/* -------------------------------------------------------------------------- */
  static async publishToggle(req, res, makePublic) {
    const user = await FilesController.userFromToken(req);
    if (!user) return res.status(401).json({ error: 'Unauthorized' });

    const filter = { _id: ObjectId(req.params.id), userId: user._id };
    const file   = await dbClient.filesCollection().findOne(filter);
    if (!file)   return res.status(404).json({ error: 'Not found' });

    await dbClient.filesCollection().updateOne(filter, {
      $set: { isPublic: makePublic },
    });
    file.isPublic = makePublic;
    return res.json(FilesController.sanitize(file));
  }

  static putPublish   (req, res) { return FilesController.publishToggle(req, res, true ); }
  static putUnpublish (req, res) { return FilesController.publishToggle(req, res, false); }

/* -------------------------------------------------------------------------- */
/* 8 – GET /files/:id/data                                                    */
/* -------------------------------------------------------------------------- */
  static async getFile(req, res) {
    const size = req.query.size;          // 100 | 250 | 500 | undefined

    /* locate the file doc (public or owner’s private) */
    const file = await dbClient.filesCollection().findOne({ _id: ObjectId(req.params.id) });
    if (!file) return res.status(404).json({ error: 'Not found' });

    /* security rules */
    const user = await FilesController.userFromToken(req);
    const owner = user && user._id.toString() === file.userId.toString();
    if (!file.isPublic && !owner) return res.status(404).json({ error: 'Not found' });

    if (file.type === 'folder') return res.status(400).json({ error: "A folder doesn't have content" });

    /* choose correct path */
    const targetPath = size ? `${file.localPath}_${size}` : file.localPath;
    try {
      const content = await fs.readFile(targetPath);
      res.setHeader('Content-Type', mime.contentType(file.name) || 'application/octet-stream');
      return res.end(content);
    } catch (err) {
      return res.status(404).json({ error: 'Not found' });
    }
  }

/* -------------------------------------------------------------------------- */
/* 5 – POST /files helper (optional)                                          */
/* -------------------------------------------------------------------------- */
  static async saveBinaryFile(dataB64) {
    await fs.mkdir(TMP_FOLDER, { recursive: true });
    const localPath = path.join(TMP_FOLDER, uuidv4());
    await fs.writeFile(localPath, Buffer.from(dataB64, 'base64'));
    return localPath;
  }
}

