// worker.js
import Queue from 'bull';
import { ObjectId } from 'mongodb';
import { promises as fs } from 'fs';
import path from 'path';
import imageThumbnail from 'image-thumbnail';
import dbClient from './utils/db';

const fileQueue = new Queue('fileQueue');

fileQueue.process(async (job) => {
  const { userId, fileId } = job.data;

  if (!fileId) throw new Error('Missing fileId');
  if (!userId) throw new Error('Missing userId');

  // wait for DB connection
  if (!dbClient.isAlive()) {
    throw new Error('MongoDB not ready');
  }

  // fetch file doc
  const file = await dbClient.db
    .collection('files')
    .findOne({
      _id: ObjectId(fileId),
      userId: ObjectId(userId),
      type: 'image',
    });

  if (!file) {
    throw new Error('File not found');
  }

  const sizes = [500, 250, 100];
  for (const width of sizes) {
    // generate thumbnail buffer
    /* imageThumbnail returns a Buffer */
    // pass full path
    // note: you can also pass { width } to options
    // ensure target directory exists
    const thumbBuffer = await imageThumbnail(file.localPath, { width });
    const thumbPath = `${file.localPath}_${width}`;
    await fs.writeFile(thumbPath, thumbBuffer);
  }

  return Promise.resolve();
});

fileQueue.on('completed', (job) => {
  // eslint-disable-next-line no-console
  console.log(`Thumbnail job ${job.id} completed`);
});

fileQueue.on('failed', (job, err) => {
  // eslint-disable-next-line no-console
  console.error(`Thumbnail job ${job.id} failed: ${err.message}`);
});

