// controllers/FilesController.js
import { promises as fs } from 'fs';
import path from 'path';
import mime from 'mime-types';
import { v4 as uuid } from 'uuid';
import dbClient from '../utils/db';
import { getUserFromXToken } from '../middlewares/authenticate';  // <- you already have that
import fileQueue from '../utils/bullQueue';                        // <- your existing queue helper

const TMP_DIR = process.env.FOLDER_PATH || '/tmp/files_manager';
const ITEMS_PER_PAGE = 20;
const VALID_TYPES = ['folder', 'file', 'image'];

/* turn a Mongo doc into the JSON format the specs want */
const doc2json = (f) => ({
  id: f._id.toString(),
  userId: f.userId.toString(),
  name: f.name,
  type: f.type,
  isPublic: f.isPublic,
  parentId: f.parentId,
});

class FilesController {
  /* -------------------------------------------------------------------- */
  /* POST /files – already worked for you; unchanged apart from two lines  */
  /* -------------------------------------------------------------------- */
  static async postUpload(req, res) {
    const token = req.header('X-Token');
    const user = await getUserFromXToken(token);
    if (!user) return res.status(401).json({ error: 'Unauthorized' });

    const {
      name, type, parentId = 0, isPublic = false, data,
    } = req.body || {};

    if (!name) return res.status(400).json({ error: 'Missing name' });
    if (!type || !VALID_TYPES.includes(type)) {
      return res.status(400).json({ error: 'Missing type' });
    }
    if (type !== 'folder' && !data) return res.status(400).json({ error: 'Missing data' });

    /* parent checks */
    if (parentId !== 0) {
      const parent = await dbClient.getFileById(parentId);
      if (!parent) return res.status(400).json({ error: 'Parent not found' });
      if (parent.type !== 'folder') return res.status(400).json({ error: 'Parent is not a folder' });
    }

    /* document to insert */
    const doc = {
      userId: user._id,
      name,
      type,
      isPublic,
      parentId,
    };

    /* write file on disk when needed */
    if (type !== 'folder') {
      await fs.mkdir(TMP_DIR, { recursive: true });
      const localPath = path.join(TMP_DIR, uuid());
      await fs.writeFile(localPath, Buffer.from(data, 'base64'));
      doc.localPath = localPath;

      /* enqueue thumbnail job for images */
      if (type === 'image') fileQueue.add({ userId: user._id, fileId: uuid(), localPath });
    }

    const result = await dbClient.filesCollection().insertOne(doc);
    return res.status(201).json({ id: result.insertedId.toString(), ...doc });
  }

  /* -------------------------------------------------------------------- */
  /* GET /files/:id                                                       */
  /* -------------------------------------------------------------------- */
  static async getShow(req, res) {
    const token = req.header('X-Token');
    const user = await getUserFromXToken(token);
    if (!user) return res.status(401).json({ error: 'Unauthorized' });

    const file = await dbClient.getFileByIdAndUserId(req.params.id, user._id);
    if (!file) return res.status(404).json({ error: 'Not found' });

    return res.json(doc2json(file));
  }

  /* -------------------------------------------------------------------- */
  /* GET /files (with parentId & page query)                              */
  /* -------------------------------------------------------------------- */
  static async getIndex(req, res) {
    const token = req.header('X-Token');
    const user = await getUserFromXToken(token);
    if (!user) return res.status(401).json({ error: 'Unauthorized' });

    const page = Number.parseInt(req.query.page, 10) || 0;
    const parentIdParam = req.query.parentId || 0;

    const match = { userId: user._id };
    match.parentId = parentIdParam === 0 ? 0 : dbClient.toObjectId(parentIdParam);

    const docs = await dbClient.filesCollection()
      .aggregate([
        { $match: match },
        { $skip: page * ITEMS_PER_PAGE },
        { $limit: ITEMS_PER_PAGE },
      ])
      .toArray();

    return res.json(docs.map(doc2json));
  }

  /* -------------------------------------------------------------------- */
  /* PUT /files/:id/publish & /unpublish                                  */
  /* (your previous working code goes here – unchanged)                   */
  /* -------------------------------------------------------------------- */

  /* -------------------------------------------------------------------- */
  /* GET /files/:id/data (size handled)                                   */
  /* -------------------------------------------------------------------- */
  static async getFile(req, res) {
    const { id } = req.params;
    const size = req.query.size;
    const file = await dbClient.getFileById(id);
    if (!file) return res.status(404).json({ error: 'Not found' });

    /* public or owner? */
    const token = req.header('X-Token');
    const user = await getUserFromXToken(token);
    const isOwner = user && user._id.toString() === file.userId.toString();
    if (!file.isPublic && !isOwner) return res.status(404).json({ error: 'Not found' });

    if (file.type === 'folder') return res.status(400).json({ error: "A folder doesn't have content" });

    const wantedPath = size ? `${file.localPath}_${size}` : file.localPath;

    try {
      const data = await fs.readFile(wantedPath);
      res.setHeader('Content-Type', mime.contentType(file.name) || 'text/plain');
      return res.end(data);
    } catch (e) {
      return res.status(404).json({ error: 'Not found' });
    }
  }
}

export default FilesController;

